import { useNavigate } from "react-router-dom";
import { useDispatch } from 'react-redux';
import { dir } from "../store/directionSlice";

const MainPage = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const move = () => {
    navigate('/word');
    dispatch(dir('right'));
  };

  return (
    <div className="basic-wrapper">
      <header>
        <input class="search" type="text" placeholder="단어를 검색해보세요!"></input>
        <button class="find_icon"><i class="fa-solid fa-magnifying-glass"></i></button>
      </header>

      <section>
        <div class="content">
          <div class="today-word" style="text-align:left">오늘의 단어</div>
          <div class="today-word-image">
            <div class="overlay"></div>
            <div class="rectangle"></div>
            <div class="question-container">
              <span class="word-category">테이크 아웃</span>
              
              <span class="word-meaning">이 무슨 말인지</span>
            </div>
          </div>
        </div>
        <div class="button-group">
          <div class="know">알아요</div>
          <div class="dont-know">몰라요</div>
        </div>
      </section>
    </div>
  );
};

export default MainPage;
